#!/usr/bin/env node

const program = require('commander');
const packageJson = require('./package.json');
const version = `Newkit development package: ${packageJson.version}`;

program
  .version(version)
  .usage('Newkit development package.');

program
  .command('start')
  .description('Start the local central platform.')
  .action(() => {
    require('./lib/newkit_start')();
  });

program
  .command('init')
  .description('Init the project as newkit project.')
  .action(() => {
    require('./lib/newkit_init')();
  });

program
  .command('build')
  .description('Build the producation modules.')
  .action(() => {
    require('./lib/newkit_build')();
  });


// 启动
program.parse(process.argv);

//如果直接使用nk，那么显示help
if (process.argv.length === 2) {
  program.outputHelp();
}

